package com.education.common.model;import java.util.List;/** * tree 树形结构实体类 * @author zengjintao * @create 2019/6/20 15:38 * @since 1.0 **/public class Tree {    private Integer parentId;    private Integer id;    private List<Tree> children;    private String label;    private Integer value;    public Tree() {    }    public Integer getParentId() {        return parentId;    }    public void setParentId(Integer parentId) {        this.parentId = parentId;    }    public Integer getId() {        return id;    }    public void setId(Integer id) {        this.id = id;    }    public List<Tree> getChildren() {        return children;    }    public void setChildren(List<Tree> children) {        this.children = children;    }    public String getLabel() {        return label;    }    public void setLabel(String label) {        this.label = label;    }    public Integer getValue() {        return value;    }    public void setValue(Integer value) {        this.value = value;    }    public Tree(int id, String label, int parentId) {       this.label = label;       this.id = id;       this.parentId = parentId;    }}